(()=>{"use strict";document.addEventListener("DOMContentLoaded",(function(){(e=>{const t=document.querySelector(e);t&&window.addEventListener("scroll",(e=>{const o=t.getBoundingClientRect(),n=window.innerHeight||document.documentElement.clientHeight;if(o.top<=0&&o.bottom>=n){const e=Math.min(1,1-Math.max(0,(n+o.top)/(o.height-n))).toFixed(2);t.style.setProperty("--scroll-percentage",e)}else o.top<=0&&o.bottom<n?t.style.setProperty("--scroll-percentage","1"):o.top>0&&t.style.setProperty("--scroll-percentage","0")}))})(".info"),(()=>{const e=document.querySelector(".header");e&&window.addEventListener("scroll",(()=>{window.scrollY>200?e.classList.add("_scrolled"):e.classList.remove("_scrolled")}))})()}))})();